import React, { Component } from "react";
import Card from "./components/Card";
import Nav from "./components/Nav"
import Title from "./components/Title";
import friends from "./friends.json";



class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    score: 0,
    topScore: 0
  };


  componentDidMount() {
    this.setState({ friends: this.shuffleData(this.state.friends) });
  }

  handleCorrectGuess = newData => {
    const { topScore, score } = this.state;
    const newScore = score + 1;
    const newTopScore = Math.max(newScore, topScore);

    this.setState({
      friends: this.shuffleData(newData),
      score: newScore,
      topSCore: newTopScore
    });
  };

  resetData = friends => {
    const resetData = friends.map(item => ({ ...item, clicked: false }));
    return this.shuffleData(resetData);
  };

  clickHandler = (event) => {
    this.setState({ message: "Click on an image to begin!" })
    let char = event.target.id;
    if (this.state.charClicked.includes(char)) {
      this.setState({ charClicked: [], score: 0, message: "Doh! You already picked that character, Try again." });
    } else {
      let charClicked = this.state.charClicked;
      charClicked.push(char);
      this.setState({ charClicked: charClicked })
      console.log(this.state.charClicked);
      this.incrementScore();
    };
    if (this.state.score === this.state.images.length) {
      this.setState({ message: "Cowabunga Dude!! You guess correctly." });
    };
    this.shuffleChar();
  }

  shuffleData = friends => {
    let i = friends.length - 1;
    while (i > 0) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = friends[i];
      friends[i] = friends[j];
      friends[j] = temp;
      i--;

    }

    return friends;

  };

  handleItemClick = id => {
    let guessedCorrectly = false;
    const newData = this.state.friends.map(item => {
      const newItem = { ...item };
      if (newItem.id === id) {
        if (!newItem.clicked) {
          newItem.clicked = true;
          guessedCorrectly = true;

        }
      }
      return newItem;
    });
    guessedCorrectly
      ? this.handleCorrectGuess(newData)
      : this.handelIncorrectguess(newData);
  };

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Title score={this.state.score} topSCore={this.state.tope}>clicky game</Title>
        {this.state.friends.map(friend => (
          <Card
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            handleClick={this.handleItemClick}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
